import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles/sidebar.css'; // Ensure this path matches your directory structure

const Sidebar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleSidebar = () => {
    setIsOpen((prev) => !prev);
  };

  const menuItems = [
    { text: 'Home', path: '/' },
    { text: 'Event', path: '/event' },
    { text: 'Task Tracker', path: '/task-tracker' },
    { text: 'Attendees', path: '/attendees' },
  ];

  return (
    <div className="sidebar-container">
      <div className={`sidebar ${isOpen ? 'open' : ''}`}>
        <button className="toggle-button" onClick={toggleSidebar}>
        'â˜°'
        </button>
        <ul className="menu">
          {menuItems.map((item) => (
            <li key={item.text}  className="menu-item">
              <Link to={item.path}>{item.text}</Link>
            </li>
          ))}
        </ul>
      </div>
      <div className={`content ${isOpen ? 'shifted' : ''}`}>
        <h1>Welcome to Sidebar Navigation</h1>
        <p>Click the button to toggle the sidebar.</p>
      </div>
    </div>
  );
};

export default Sidebar;
