import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/Attendees.css";

const Attendees = () => {
  const [attendees, setAttendees] = useState([]);
  const [newAttendee, setNewAttendee] = useState({ name: ""});
  const [editingAttendee, setEditingAttendee] = useState(null);

  // Fetch attendees from the server
  const fetchAttendees = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/getAttendees");
      setAttendees(response.data);
    } catch (error) {
      console.error("Error fetching attendees:", error);
    }
  };

  // Add a new attendee
  const addAttendee = async () => {
    try {
      await axios.post("http://localhost:5000/api/postAttendee", newAttendee);
      fetchAttendees();
      setNewAttendee({ name: "", email: "" });
    } catch (error) {
      console.error("Error adding attendee:", error);
    }
  };

  // Update attendee details


  // Delete an attendee
  const deleteAttendee = async (id) => {
    try {
      await axios.delete("http://localhost:5000/api/deleteAttendee", { data: { id } });
      fetchAttendees();
    } catch (error) {
      console.error("Error deleting attendee:", error);
    }
  };

  useEffect(() => {
    fetchAttendees();
  }, []);

  return (
    <div className="dashboard">
      <h1>Attendees Dashboard</h1>
      <div className="add-attendee">
        <h2>Add Attendee</h2>
        <input
          type="text"
          placeholder="Name"
          value={newAttendee.name}
          onChange={(e) => setNewAttendee({ ...newAttendee, name: e.target.value })}
        />
        <input
          type="email"
          placeholder="Email"
          value={newAttendee.email}
          onChange={(e) => setNewAttendee({ ...newAttendee, email: e.target.value })}
        />
        <button onClick={addAttendee}>Add</button>
      </div>

      <div className="attendee-list">
        <h2>Attendees List</h2>
        {attendees.map((attendee) => (
          <div key={attendee._id} className="attendee-item">
            {editingAttendee && editingAttendee._id === attendee._id ? (
              <>
                <input
                  type="text"
                  value={editingAttendee.name}
                  onChange={(e) =>
                    setEditingAttendee({ ...editingAttendee, name: e.target.value })
                  }
                />
 
              </>
            ) : (
              <>
                <span>{attendee.name}</span>
                <button onClick={() => setEditingAttendee(attendee)}>Edit</button>
                <button onClick={() => deleteAttendee(attendee._id)}>Delete</button>
              </>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Attendees;
