import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/Event.css';

export default function Event() {
  const [events, setEvents] = useState([]);
  const [tasks, setTasks] = useState({});
  const [editingEventName, setEditingEventName] = useState(null);
  const [editField, setEditField] = useState('');
  const [editValue, setEditValue] = useState('');
  const [newEvent, setNewEvent] = useState({
    name: '',
    date: '',
    location: '',
    description: '',
  });

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/getEvents');
      setEvents(response.data);
    } catch (error) {
      console.error('Error fetching events:', error);
    }
  };

  const fetchTasksForEvent = async (eventName) => {
    try {
      const response = await axios.post('http://localhost:5000/getEventTasks', {
        name: eventName,
      });
      setTasks((prevTasks) => ({
        ...prevTasks,
        [eventName]: response.data, // Update tasks for the specific event
      }));
    } catch (error) {
      console.error('Error fetching tasks for event:', error);
    }
  };

  const toggleTasks = (eventName) => {
    if (tasks[eventName]) {
      setTasks((prevTasks) => {
        const updatedTasks = { ...prevTasks };
        delete updatedTasks[eventName]; // Remove tasks to collapse the view
        return updatedTasks;
      });
    } else {
      fetchTasksForEvent(eventName);
    }
  };

  const addEvent = async () => {
    try {
      if (!newEvent.name || !newEvent.date || !newEvent.location || !newEvent.description) {
        alert('All fields are required!');
        return;
      }
      await axios.post('http://localhost:5000/api/postEvent', newEvent);
      fetchEvents();
      setNewEvent({ name: '', date: '', location: '', description: '' });
    } catch (error) {
      console.error('Error adding event:', error);
    }
  };

  return (
    <div className="event-page">
      <h1>Event Management</h1>

      <div className="add-event-form">
        <h2>Add New Event</h2>
        <input
          type="text"
          placeholder="Event Name"
          value={newEvent.name}
          onChange={(e) => setNewEvent({ ...newEvent, name: e.target.value })}
        />
        <input
          type="date"
          value={newEvent.date}
          onChange={(e) => setNewEvent({ ...newEvent, date: e.target.value })}
        />
        <input
          type="text"
          placeholder="Location"
          value={newEvent.location}
          onChange={(e) => setNewEvent({ ...newEvent, location: e.target.value })}
        />
        <textarea
          placeholder="Description"
          value={newEvent.description}
          onChange={(e) => setNewEvent({ ...newEvent, description: e.target.value })}
        ></textarea>
        <button onClick={addEvent}>Add Event</button>
      </div>

      <div className="event-table">
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Date</th>
              <th>Location</th>
              <th>Description</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {events.map((event) => (
              <React.Fragment key={event._id}>
                <tr>
                  <td>{event.name}</td>
                  <td>{new Date(event.date).toLocaleDateString()}</td>
                  <td>{event.location}</td>
                  <td>{event.description}</td>
                  <td>
                    <button onClick={() => toggleTasks(event.name)}>
                      {tasks[event.name] ? 'Hide Tasks' : 'Show Tasks'}
                    </button>
                  </td>
                </tr>
                {tasks[event.name] && (
                  <tr>
                    <td colSpan="5">
                      <ul>
                        {tasks[event.name].map((task, index) => (
                          <li key={index}>{task}</li>
                        ))}
                      </ul>
                    </td>
                  </tr>
                )}
              </React.Fragment>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
